.ec-container {
  height: calc(100vh - 100px);
}

.ec-blobs {
  max-width: 1000px;
  position: relative;
  height: 100%;
}

.ec-cards {
  height: 100%;
}

.ec-blob {
  .v-btn {
    text-align: left;
    position: absolute;
    bottom: 0;
  }

  rect {
    opacity: 0;
    transition: opacity 0.3s cubic-bezier(0.55, 0.09, 0.68, 0.53);
  }

  &:hover rect {
    opacity: 50%;
  }

  position: absolute;
  &-1 {
    top: 10%;
    left: 5%;
